import React, { Component } from 'react'
import { useContext } from 'react';
import { RoomContext, RoomConsumer } from '../context';
import Title from './Title';
// get all unique values
const getUnique = (items, value) => {
    return [...new Set(items.map(item => item[value]))];
}
export default function RoomFilter({ rooms }) {
    const context = useContext(RoomContext);
    const {

        handleChange, type,
        capacity,
        price,
        minPrice,
        maxSize,
        maxPrice,
        minSize,
        breakfast,
        pets
    } = context;
    // get unique types
    let types = getUnique(rooms, "type");// prendo tutti i valori di type differenti e li metto in un array
    // add all

    types = ["all", ...types];

    types = types.map((item, index) => {
        return (<option value={item} key={index}>{item}</option>)
    })
    let people = getUnique(rooms, "capacity");
    people = people.map((item, index) => {
        return <option value={item} key={index}>{item}</option>
    })
    return (

        <div>
            <section className="filter-container">
                <Title title="search rooms" />
                <form className="filter-form">
                    {/*select type*/}
                    <div className="form-group">
                        <label htmlFor="type" >room type</label>
                        <select name="type" id="type" value={type} className="form-control"
                            onChange={handleChange} >
                            {types}
                        </select>

                    </div>
                    {/* end select type*/}
                    {/*select guest*/}
                    <div className="form-group">
                        <label htmlFor="guest" >room type</label>
                        <select name="guest" id="type" value={capacity} className="form-control"
                            onChange={handleChange} >
                            {people}
                        </select>

                    </div>
                    {/* end select guest*/}
                </form>

            </section>
        </div>



    )


}
