import React, { Component } from 'react'
import items from './data';
const RoomContext = React.createContext();

//<RoomContext.Provider value={'hello'}
export default class RoomProvider extends Component {
    state = {
        rooms: [],
        sortedRomm: [],
        featuredRooms: [],
        loading: false,
        type:'all', //searching
            capacity:1,
            price:0,
            minPrice:0,
            maxSize:0,
            maxPrice:0,
            minSize:0,
            breakfast:false,
            pets: false

    }

    // function getData
    componentDidMount() {
        let rooms = this.formatData(items);

        let featuredRooms = rooms.filter(room => room.featured === true);
        let maxPrice = Math.max(...rooms.map(item=> 
            item.price));
         let maxSize = Math.max(...rooms.map(item=> 
                item.size));


        this.setState({
            rooms,
            featuredRooms,
            sortedRomm: rooms,
            loading: true,
            maxPrice,
            maxSize

        })
    }

    formatData(items) {
        let tempItems = items.map(item => {
            let id = item.sys.id;
            let images = item.fields.images.map(image =>
                image.fields.file.url);
            let room = { ...item.fields, images, id };
            return room;


        });
        return tempItems
    }
    getRoom = (slug) => {
        let tempRooms = [...this.state.rooms];
        const room = tempRooms.find(room => room.slug === slug);
        return room;

    }

    handleChange = event => {
        const target = event.target;
        const value = event.type === 'checkbox' ? 
        target.checked:target.value
        const name = event.target.name
        console.log('il valore è '+value);
        console.log('target'+target);

       this.setState({
           [name] : value

       }, this.filterRoom)

    };
    filterRoom = ()=> {
        let {
            rooms, type, capacity, price, minPrice, minSize, maxSize, maxPrice, breakfast, pets
        } = this.state;

        let tempRooms = [...rooms];
        if(type !=='all') {
            tempRooms= tempRooms.filter(room => room.type ===type);
        } 
        this.setState({
            sortedRomm : tempRooms
        })
    }
    render() {
        return <RoomContext.Provider value={{ ...this.state, getRoom: this.getRoom }}>
            {this.props.children}

        </RoomContext.Provider>


    }
}
const RoomConsumer = RoomContext.Consumer;

export function withRoomConsumer(Component){
           return function ConsumerWrapper(props) {
               return <RoomConsumer>
               {value=> <Component {...props} context={value}/>}               
                }

               </RoomConsumer>
           }

}
export { RoomConsumer, RoomProvider, RoomContext }
